import "./styles.css";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getListAction } from "./redux/action";
import { addItems } from "./redux/action";
import { deleteData } from "./redux/action";

export default function App() {
  const selector = useSelector((state) => state);
  console.log(selector, "ajjayi");
  const [input, setInput] = useState({
    itemName: "",
    itemDescription: "",
    itemPrice: "",
    itemImage: ""
  });

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getListAction());
  }, []);

  const onChangeHandler = (e) => {
    const value = e.target.value;
    const name = e.target.name;

    setInput({ ...input, [name]: value });
  };

  const onClickHandler = () => {
    dispatch(addItems(input));
  };

  const deleteHandler = (index) => {
    dispatch(deleteData(index));
  };

  return (
    <div className="App">

    <div className="container">
    <div className="row">

    <div>
        <h1>Add New Product</h1>
        <div>
          <div>
            <label>Product Name </label>
            <input
              type="text"
              name="itemName"
              onChange={onChangeHandler}
              value={input.itemName}
            />
          </div>

          <div>
            <label>Description </label>
            <textarea
              type="text"
              name="itemDescription"
              onChange={onChangeHandler}
              value={input.itemDescription}
            />
          </div>

          <div>
            <label>Price </label>
            <input
              type="number"
              name="itemPrice"
              onChange={onChangeHandler}
              value={input.itemPrice}
            />
          </div>

          <div>
            <label>Image </label>
            <input
              type="text"
              name="itemImage"
              onChange={onChangeHandler}
              value={input.itemImage}
              placeholder="Paste Image URL Here"
            />
          </div>
        </div>

        <button onClick={onClickHandler}>Add</button>
      </div>

      <div>
<select name="Vendor" id="Vendor">
    <option value="Shree Textile">Shree Textile</option>
    <option value="Crown Ltd">Crown Ltd.</option>
    <option value="Lucky Goods">Lucky Goods</option>

  </select>

  <select name="Type" id="Type">
    <option value="Shoes">Shoes</option>
    <option value="furniture">Furniture</option>
    <option value="laptop">Laptop</option>


  </select>


</div>

<ul class="card-list col-12">
        {selector?.rootReducers?.productList?.data?.map((val, index) => {
          return (
            <>
              <li class="card" style={{ marginTop: "50px" }}>
                <img src={val?.img} alt="img" className="product-image" />
                <h2>Name:- {val?.productName}</h2>
                <h2>Price:- {val?.price}</h2>


                {/* 
                <p>Description:- {val?.description}</p>
                <p>Price:- {val?.price} </p> */}
                <button
             className="card-list-btn"
                  onClick={() => deleteHandler(index)}
                >
                  Delete
                </button>
              </li>
            </>
          );
        })}
      </ul>
    </div>

    </div>







    </div>
  );
}
